 import React, { useState, useEffect } from "react";

function App() {
  const [files, setFiles] = useState([
    { name: "main.py", code: "print('Hello from Python!')" },
  ]);
  const [currentFileIndex, setCurrentFileIndex] = useState(0);
  const [output, setOutput] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [theme, setTheme] = useState("white");

  const currentFile = files[currentFileIndex];

  const updateCode = (newCode) => {
    const updatedFiles = [...files];
    updatedFiles[currentFileIndex] = { ...updatedFiles[currentFileIndex], code: newCode };
    setFiles(updatedFiles);
  };

  const runCode = async () => {
    setLoading(true);
    setOutput("");
    setError("");
    try {
      const res = await fetch("http://localhost:5000/run", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code: currentFile.code }),
      });
      const data = await res.json();
      setOutput(data.output || "");
      setError(data.error || "");
    } catch (err) {
      setError("Fetch error: " + err.message);
    }
    setLoading(false);
  };

  const saveFile = () => {
    const element = document.createElement("a");
    const file = new Blob([currentFile.code], { type: "text/plain" });
    element.href = URL.createObjectURL(file);
    element.download = currentFile.name;
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  const addNewFile = () => {
    const newFileName = prompt("Enter new file name:", `file${files.length + 1}.py`);
    if (!newFileName) return;

    if (files.find((f) => f.name === newFileName)) {
      alert("File name already exists!");
      return;
    }

    const newFiles = [...files, { name: newFileName, code: "# New Python file\n" }];
    setFiles(newFiles);
    setCurrentFileIndex(newFiles.length - 1);
    setOutput("");
    setError("");
  };

  const deleteFile = (index) => {
    if (files.length === 1) {
      alert("Cannot delete the only remaining file.");
      return;
    }
    const confirmDelete = window.confirm(`Delete file "${files[index].name}"?`);
    if (!confirmDelete) return;

    const newFiles = files.filter((_, i) => i !== index);

    let newCurrentIndex = currentFileIndex;
    if (index < currentFileIndex) {
      newCurrentIndex = currentFileIndex - 1;
    } else if (index === currentFileIndex) {
      newCurrentIndex = 0;
    }

    setFiles(newFiles);
    setCurrentFileIndex(newCurrentIndex);
    setOutput("");
    setError("");
  };

  // New enhanced switchFile handles ctrl/meta click to add file
  const onFileTabClick = (index, event) => {
    if (event.ctrlKey || event.metaKey) {
      // Ctrl or Cmd + click: add new file
      event.preventDefault();
      addNewFile();
    } else {
      setCurrentFileIndex(index);
      setOutput("");
      setError("");
    }
  };

  // Add Ctrl+S / Cmd+S keydown listener
  useEffect(() => {
    const handleKeyDown = (e) => {
      if ((e.ctrlKey || e.metaKey) && e.key.toLowerCase() === "s") {
        e.preventDefault();
        saveFile();
      }
    };

    window.addEventListener("keydown", handleKeyDown);

    // Cleanup listener on unmount
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [currentFile]); // re-register when currentFile changes

  return (
    <div className={`container ${theme}`}>
      <h1 className="title">üêç Python Code Runner</h1>

      <div className="theme-selector">
        <label htmlFor="theme-select">Select Theme: </label>
        <select
          id="theme-select"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
          aria-label="Select editor theme"
        >
          <option value="white">White</option>
          <option value="light">Light</option>
          <option value="dark">Dark</option>
        </select>
      </div>

      <div className="file-tabs" role="tablist" aria-label="File tabs">
        {files.map((file, idx) => (
          <div
            key={file.name}
            className={`file-tab-wrapper ${idx === currentFileIndex ? "active" : ""}`}
            role="tab"
            aria-selected={idx === currentFileIndex}
            tabIndex={idx === currentFileIndex ? 0 : -1}
          >
            <button
              className="file-tab"
              onClick={(e) => onFileTabClick(idx, e)}
              title={`Switch to ${file.name} (Ctrl/Cmd+Click to add new file)`}
            >
              {file.name}
            </button>
            {files.length > 1 && (
              <button
                className="delete-file-btn"
                onClick={() => deleteFile(idx)}
                title={`Delete ${file.name}`}
                aria-label={`Delete ${file.name}`}
              >
                √ó
              </button>
            )}
          </div>
        ))}
        <button
          className="file-tab add-file-btn"
          onClick={addNewFile}
          title="Add new file"
          aria-label="Add new file"
        >
          + Add File
        </button>
      </div>

      <textarea
        rows={15}
        value={currentFile.code}
        onChange={(e) => updateCode(e.target.value)}
        className={`textarea ${theme}`}
        aria-label={`Code editor for ${currentFile.name}`}
      />

      <div className="button-group">
        <button onClick={runCode} className="button" disabled={loading}>
          {loading ? "‚è≥ Running..." : "‚ñ∂Ô∏è Run Code"}
        </button>
        <button onClick={saveFile} className="button">
          üíæ Save File
        </button>
      </div>

      <div className="section output-section" aria-live="polite" aria-atomic="true">
        <h3>üì§ Output:</h3>
        <pre className="output">{output || "No output yet."}</pre>
        {error && (
          <>
            <h3>‚ùå Error:</h3>
            <pre className="error">{error}</pre>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
