 import React, { useState } from "react";
import AceEditor from "react-ace";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/theme-monokai";
import "./App.css";

function App() {
  const [code, setCode] = useState("print('Hello, World!')");
  const [input, setInput] = useState("");
  const [output, setOutput] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [theme, setTheme] = useState("monokai");

  const runCode = async () => {
    setLoading(true);
    setOutput("");
    setError("");
    try {
      const res = await fetch("http://localhost:5000/run", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code, input }),
      });
      const data = await res.json();
      setOutput(data.output || "");
      setError(data.error || "");
    } catch (err) {
      setError("Fetch error: " + err.message);
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1 className="title">üêç Python Online Compiler</h1>

      <div className="theme-selector">
        <label>Editor Theme: </label>
        <select value={theme} onChange={(e) => setTheme(e.target.value)}>
          <option value="monokai">Dark (Monokai)</option>
          <option value="github">Light (GitHub)</option>
        </select>
      </div>

      <AceEditor
        mode="python"
        theme={theme}
        name="code-editor"
        fontSize={16}
        width="100%"
        height="300px"
        value={code}
        onChange={(value) => setCode(value)}
        editorProps={{ $blockScrolling: true }}
      />

      <textarea
        rows={4}
        className="input-box"
        placeholder="Enter input (stdin) here..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
      ></textarea>

      <button className="button" onClick={runCode} disabled={loading}>
        {loading ? "‚è≥ Running..." : "‚ñ∂Ô∏è Run Code"}
      </button>

      <div className="section">
        <h3>üì§ Output:</h3>
        <pre className="output">{output}</pre>
      </div>

      {error && (
        <div className="section">
          <h3>‚ùå Error:</h3>
          <pre className="error">{error}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
