 import React, { useState } from "react";
import AceEditor from "react-ace";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/theme-monokai";
import "ace-builds/src-noconflict/theme-github";
import "ace-builds/src-noconflict/theme-xcode";
import "./App.css";

function App() {
  const [code, setCode] = useState("print('Hello from Python!')");
  const [input, setInput] = useState("");
  const [output, setOutput] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [theme, setTheme] = useState("white");

  const getAceTheme = () => {
    if (theme === "dark") return "monokai";
    if (theme === "light") return "xcode";
    return "github"; // white
  };

  const runCode = async () => {
    setLoading(true);
    setOutput("");
    setError("");
    try {
      const res = await fetch("http://localhost:5000/run", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code, input }),
      });
      const data = await res.json();
      setOutput(data.output || "");
      setError(data.error || "");
    } catch (err) {
      setError("Fetch error: " + err.message);
    }
    setLoading(false);
  };

  return (
    <div className={`container ${theme}`}>
      <h1 className="title">üêç Python Compiler</h1>

      <div className="theme-selector">
        <label>Select Theme: </label>
        <select value={theme} onChange={(e) => setTheme(e.target.value)}>
          <option value="white">White</option>
          <option value="light">Light</option>
          <option value="dark">Dark</option>
        </select>
      </div>

      <AceEditor
        mode="python"
        theme={getAceTheme()}
        name="code-editor"
        fontSize={16}
        value={code}
        onChange={(value) => setCode(value)}
        width="100%"
        height="300px"
        className="editor"
      />

      <textarea
        rows={4}
        className="input-box"
        placeholder="Enter custom input..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
      ></textarea>

      <br />
      <button onClick={runCode} className="button" disabled={loading}>
        {loading ? "‚è≥ Running..." : "‚ñ∂Ô∏è Run Code"}
      </button>

      <div className="section">
        <h3>üì§ Output:</h3>
        <pre className="output">{output}</pre>
      </div>

      {error && (
        <div className="section">
          <h3>‚ùå Error:</h3>
          <pre className="error">{error}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
