 import React, { useState, useRef } from "react";
import "./App.css";

function App() {
  const [code, setCode] = useState("print('Hello from Python!')");
  const [output, setOutput] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [theme, setTheme] = useState("white");

  const fileInputRef = useRef(null);

  // Run code as before
  const runCode = async () => {
    setLoading(true);
    setOutput("");
    setError("");
    try {
      const res = await fetch("http://localhost:5000/run", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code }),
      });
      const data = await res.json();
      setOutput(data.output || "");
      setError(data.error || "");
    } catch (err) {
      setError("Fetch error: " + err.message);
    }
    setLoading(false);
  };

  // Save code to file
  const saveFile = () => {
    const blob = new Blob([code], { type: "text/plain;charset=utf-8" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "code.py";
    link.click();
    URL.revokeObjectURL(link.href);
  };

  // Open file dialog
  const openFileDialog = () => {
    if (fileInputRef.current) {
      fileInputRef.current.value = null; // reset input so same file can be uploaded again if needed
      fileInputRef.current.click();
    }
  };

  // Load file content into editor
  const loadFile = (event) => {
    const file = event.target.files[0];
    if (file && file.name.endsWith(".py")) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setCode(e.target.result);
        setOutput("");
        setError("");
      };
      reader.readAsText(file);
    } else {
      alert("Please select a valid Python (.py) file.");
    }
  };

  // New file (clear editor)
  const newFile = () => {
    if (
      window.confirm(
        "Are you sure you want to create a new file? Unsaved changes will be lost."
      )
    ) {
      setCode("");
      setOutput("");
      setError("");
    }
  };

  return (
    <div className={`container ${theme}`}>
      <h1 className="title">üêç Python Code Runner</h1>

      <div className="theme-selector">
        <label htmlFor="theme-select">Select Theme: </label>
        <select
          id="theme-select"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
        >
          <option value="white">White</option>
          <option value="light">Light</option>
          <option value="dark">Dark</option>
        </select>
      </div>

      <textarea
        id="code-input"
        rows={12}
        value={code}
        onChange={(e) => setCode(e.target.value)}
        className={`textarea ${theme}`}
        disabled={loading}
        aria-label="Code editor"
      />

      <div className="button-group" style={{ marginTop: "15px" }}>
        <button onClick={runCode} className="button" disabled={loading}>
          {loading ? "‚è≥ Running..." : "‚ñ∂Ô∏è Run Code"}
        </button>
        <button onClick={saveFile} className="button" style={{ marginLeft: 10 }}>
          üíæ Save File
        </button>
        <button onClick={openFileDialog} className="button" style={{ marginLeft: 10 }}>
          üìÇ Open File
        </button>
        <button onClick={newFile} className="button" style={{ marginLeft: 10 }}>
          üÜï New File
        </button>
      </div>

      {/* Hidden file input for opening files */}
      <input
        type="file"
        accept=".py"
        ref={fileInputRef}
        style={{ display: "none" }}
        onChange={loadFile}
      />

      <div className="section">
        <h3>üì§ Output:</h3>
        <pre className="output">{output}</pre>
      </div>

      {error && (
        <div className="section">
          <h3>‚ùå Error:</h3>
          <pre className="error">{error}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
