 import React, { useState } from "react";
import "./App.css"; // Make sure you include the CSS you want!

function App() {
  const [code, setCode] = useState("print('Hello from Python!')");
  const [output, setOutput] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [theme, setTheme] = useState("white"); // Themes: white, light, dark

  const runCode = async () => {
    setLoading(true);
    setOutput("");
    setError("");
    try {
      const res = await fetch("http://localhost:5000/run", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code }),
      });
      const data = await res.json();
      setOutput(data.output || "");
      setError(data.error || "");
    } catch (err) {
      setError("Fetch error: " + err.message);
    }
    setLoading(false);
  };

  return (
    <div className={`container ${theme}`}>
      <h1 className="title">üêç Python Code Runner</h1>

      <div className="theme-selector">
        <label htmlFor="theme-select">Select Theme: </label>
        <select
          id="theme-select"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
        >
          <option value="white">White</option>
          <option value="light">Light</option>
          <option value="dark">Dark</option>
        </select>
      </div>

      <textarea
        id="code-input"
        rows={12}
        value={code}
        onChange={(e) => setCode(e.target.value)}
        className={`textarea ${theme}`}
        disabled={loading}
        aria-label="Code editor"
      />

      <button
        onClick={runCode}
        className="button"
        disabled={loading}
        aria-busy={loading}
        aria-live="polite"
      >
        {loading ? "‚è≥ Running..." : "‚ñ∂Ô∏è Run Code"}
      </button>

      <div className="section">
        <h3>üì§ Output:</h3>
        <pre className="output">{output}</pre>
      </div>

      {error && (
        <div className="section">
          <h3>‚ùå Error:</h3>
          <pre className="error">{error}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
