 import React, { useState } from "react";
import "./App.css"; // Ensure this CSS file is available

function App() {
  const [code, setCode] = useState("print('Hello from Python!')");
  const [output, setOutput] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [bgColor, setBgColor] = useState("#ffffff");

  const runCode = async () => {
    setLoading(true);
    setOutput("");
    setError("");
    try {
      const res = await fetch("http://localhost:5000/run", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code }),
      });
      const data = await res.json();
      setOutput(data.output || "");
      setError(data.error || "");
    } catch (err) {
      setError("Fetch error: " + err.message);
    }
    setLoading(false);
  };

  const changeColor = async () => {
    try {
      const res = await fetch("http://localhost:5000/change-color", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ color: "#e0f7fa" }), // Example color
      });
      const data = await res.json();
      if (data.success && data.color) {
        setBgColor(data.color);
        alert("üé® Color changed successfully!");
      } else {
        alert("‚ö†Ô∏è Failed to change color.");
      }
    } catch (err) {
      alert("Error changing color: " + err.message);
    }
  };

  return (
    <div className="container" style={{ backgroundColor: bgColor }}>
      <h1 className="title">üêç Python Code Runner</h1>

      <textarea
        rows={12}
        value={code}
        onChange={(e) => setCode(e.target.value)}
        className="textarea"
      />

      <br />
      <button onClick={runCode} className="button" disabled={loading}>
        {loading ? "‚è≥ Running..." : "‚ñ∂Ô∏è Run Code"}
      </button>

      <button onClick={changeColor} className="button">
        üé® Change Color
      </button>

      <div className="section">
        <h3>üì§ Output:</h3>
        <pre className="output">{output}</pre>
      </div>

      {error && (
        <div className="section">
          <h3>‚ùå Error:</h3>
          <pre className="error">{error}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
